    private void tablaClienteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablaClienteMouseClicked
        txtNombre.setText(tablaCliente.getValueAt(tablaCliente.getSelectedRow(), 1).toString());
        txtEdad.setText(tablaCliente.getValueAt(tablaCliente.getSelectedRow(), 2).toString());
        txtSalario.setText(tablaCliente.getValueAt(tablaCliente.getSelectedRow(), 3).toString());
        txtTelefono.setText(tablaCliente.getValueAt(tablaCliente.getSelectedRow(), 4).toString());
        perSeleccion = Integer.parseInt(tablaCliente.getValueAt(tablaCliente.getSelectedRow(), 0).toString());
    }//GEN-LAST:event_tablaClienteMouseClicked

    private void btnEditarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnEditarMouseClicked
        Cliente cli = new Cliente();
        cli.setNombre(txtNombre.getText());
        cli.setEdad(Integer.parseInt(txtEdad.getText()));
        cli.setSueldo(Float.parseFloat((txtSalario.getText())));
        cli.setTelefono(txtTelefono.getText());
        cli.setCodigo(perSeleccion);
        int dialogResult = JOptionPane.showConfirmDialog(null, "Esta seguro de Editar este cliente", "Sistema Reserva", JOptionPane.YES_NO_OPTION);
        if (dialogResult == JOptionPane.YES_OPTION) {
            cli.editarCliente(cli);
            limpiar();
            cargarDatos();
        } else {
            limpiar();
        }
    }//GEN-LAST:event_btnEditarMouseClicked

