    private void btnEliminarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnEliminarMouseClicked
       Cliente cli = new Cliente();
        int dialogResult = JOptionPane.showConfirmDialog(null, "Esta seguro de eliminar este ciente", "Sistema Reserva", JOptionPane.YES_NO_OPTION);
        if (dialogResult == JOptionPane.YES_OPTION) {
            cli.eliminarCliente(perSeleccion);
            limpiar();
            cargarDatos();
        } else {
            limpiar();
        }
    }//GEN-LAST:event_btnEliminarMouseClicked

    private void btnLimpiarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLimpiarMouseClicked
        limpiar();
    }//GEN-LAST:event_btnLimpiarMouseClicked
     private void limpiar() {
        txtNombre.setText("");
        txtEdad.setText("");
        txtSalario.setText("");
        txtTelefono.setText("");
        perSeleccion = 0;

    }
    private void btnInsertarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnInsertarMouseClicked
        Cliente cli = new Cliente();
        cli.setNombre(txtNombre.getText());
        cli.setEdad(Integer.parseInt(txtEdad.getText()));
        cli.setSueldo(Float.parseFloat(txtSalario.getText()));
        cli.setTelefono(txtTelefono.getText());
        cli.insertarCliente(cli);
        limpiar();
        cargarDatos();
    }//GEN-LAST:event_btnInsertarMouseClicked
public void cargarDatos() {
        DefaultTableModel modelo = (DefaultTableModel) tablaCliente.getModel();
        while (modelo.getRowCount() > 0) {
            modelo.removeRow(0);
        }
        Object Fila[] = new Object[tablaCliente.getColumnCount()];
        Cliente cli = new Cliente();
        for (Cliente c : cli.mostrarCliente()) {
            Fila[0] = c.getCodigo();
            Fila[1] = c.getNombre();
            Fila[2] = c.getEdad();
            Fila[3] = c.getSueldo();
            Fila[4]=c.getTelefono();
            modelo.addRow(Fila);
        }
        tablaCliente.setModel(modelo);
    }
